import { describe, it, expect } from 'vitest';
import {
  isDisabledName,
  stripDisabledPrefix,
  toggleDisabledInPath,
  DISABLED_PREFIX,
} from './disabledPrefix';

describe('disabledPrefix', () => {
  describe('isDisabledName', () => {
    it('should identify canonical prefix', () => {
      expect(isDisabledName('DISABLED MyMod')).toBe(true);
    });

    it('should identify case variants', () => {
      expect(isDisabledName('disabled MyMod')).toBe(true);
      expect(isDisabledName('DiSaBLeD_MyMod')).toBe(true);
      expect(isDisabledName('dis MyMod')).toBe(true);
      expect(isDisabledName('disable-MyMod')).toBe(true);
    });

    it('should reject normal names', () => {
      expect(isDisabledName('MyMod')).toBe(false);
      expect(isDisabledName('NotDisabled')).toBe(false);
      // 'distance_mod' starts with 'dis' so the regex technically matches it
      expect(isDisabledName('distance_mod')).toBe(true);
      expect(isDisabledName('Some_disable_mod')).toBe(false); // Not at start
    });
  });

  describe('stripDisabledPrefix', () => {
    it('should strip canonical prefix', () => {
      expect(stripDisabledPrefix('DISABLED MyMod')).toBe('MyMod');
    });

    it('should strip variations and separators', () => {
      expect(stripDisabledPrefix('disabled_MyMod')).toBe('MyMod');
      expect(stripDisabledPrefix('dis-MyMod')).toBe('MyMod');
      expect(stripDisabledPrefix('disable   MyMod')).toBe('MyMod');
    });

    it('should leave normal names alone', () => {
      expect(stripDisabledPrefix('MyMod')).toBe('MyMod');
    });
  });

  describe('toggleDisabledInPath', () => {
    it('should disable a previously enabled path', () => {
      expect(toggleDisabledInPath('mods/Character/MyMod', false)).toBe(
        'mods/Character/DISABLED MyMod',
      );
    });

    it('should enable a previously disabled path', () => {
      expect(toggleDisabledInPath('mods/Character/DISABLED MyMod', true)).toBe(
        'mods/Character/MyMod',
      );
    });

    it('should not double-disable a path', () => {
      expect(toggleDisabledInPath('mods/Character/DISABLED MyMod', false)).toBe(
        'mods/Character/DISABLED MyMod',
      );
      expect(toggleDisabledInPath('mods/Character/disabled_MyMod', false)).toBe(
        'mods/Character/disabled_MyMod',
      );
    });

    it('should handle windows paths implicitly if split by / or \\', () => {
      expect(toggleDisabledInPath('mods\\Character\\MyMod', false)).toBe(
        'mods/Character/DISABLED MyMod',
      );
    });
  });
});

import { describe, it, expect, vi } from 'vitest';
import { cn, getFileUrl } from './utils';
import * as tauriCore from '@tauri-apps/api/core';

vi.mock('@tauri-apps/api/core', () => ({
  convertFileSrc: vi.fn((path: string) => `asset://${path}`),
}));

describe('utils', () => {
  describe('cn', () => {
    it('should merge tailwind classes properly', () => {
      // Normal clsx behavior
      expect(cn('class1', 'class2')).toBe('class1 class2');
      // Tailwind merge behavior (resolving conflicts)
      expect(cn('p-4', 'p-8')).toBe('p-8');
      // Conditional behavior
      expect(cn('class1', false ? 'class2' : '', 'class3')).toBe('class1 class3');
    });
  });

  describe('getFileUrl', () => {
    it('should use Tuaris convertFileSrc', () => {
      const result = getFileUrl('/test/path/image.png');
      expect(tauriCore.convertFileSrc).toHaveBeenCalledWith('/test/path/image.png');
      expect(result).toBe('asset:///test/path/image.png');
    });
  });
});

import { describe, it, expect, beforeEach, vi } from 'vitest';
import { useAppStore } from './useAppStore';
import { act } from '@testing-library/react';

// Mock localStorage and tauri api
vi.stubGlobal('localStorage', {
  getItem: vi.fn(),
  setItem: vi.fn(),
  removeItem: vi.fn(),
});

vi.mock('@tauri-apps/api/core', () => ({
  invoke: vi.fn().mockResolvedValue({}),
}));

describe('useAppStore', () => {
  beforeEach(() => {
    // Reset store state
    act(() => {
      useAppStore.setState({
        activeGameId: null,
        safeMode: true,
        autoCloseLauncher: false,
        isStoreInitialized: false,
        workspaceView: 'dashboard',
        currentPath: [],
        activeCollectionId: null,
        selectedObject: null,
        gridSelection: new Set(),
        leftPanelWidth: 260,
        rightPanelWidth: 320,
        selectedObjectType: null,
        sidebarSearchQuery: '',
        collapsedCategories: new Set(),
        mobileActivePane: 'sidebar',
        isPreviewOpen: true,
        sortField: 'name',
        sortOrder: 'asc',
        viewMode: 'grid',
        explorerSubPath: undefined,
        explorerSearchQuery: '',
        explorerScrollOffset: 0,
        conflictDialog: { open: false, conflict: null },
      });
    });
    vi.clearAllMocks();
  });

  it('initializes with default state', () => {
    const state = useAppStore.getState();
    expect(state.activeGameId).toBeNull();
    expect(state.safeMode).toBe(true);
    expect(state.workspaceView).toBe('dashboard');
  });

  it('setWorkspaceView updates view', () => {
    act(() => {
      useAppStore.getState().setWorkspaceView('mods');
    });
    expect(useAppStore.getState().workspaceView).toBe('mods');
  });

  it('setCurrentPath updates path', () => {
    act(() => {
      useAppStore.getState().setCurrentPath(['a', 'b']);
    });
    expect(useAppStore.getState().currentPath).toEqual(['a', 'b']);
  });

  it('toggleGridSelection adds items (single select mode)', () => {
    act(() => {
      useAppStore.getState().toggleGridSelection('item1');
    });
    expect(useAppStore.getState().gridSelection.has('item1')).toBe(true);
    expect(useAppStore.getState().mobileActivePane).toBe('details');

    // In single select mode (multi=false), clicking again does not unselect
    act(() => {
      useAppStore.getState().toggleGridSelection('item1');
    });
    expect(useAppStore.getState().gridSelection.has('item1')).toBe(true);
  });

  it('toggleGridSelection toggles items off when multi=true', () => {
    act(() => {
      useAppStore.getState().toggleGridSelection('item1', true);
    });
    expect(useAppStore.getState().gridSelection.has('item1')).toBe(true);

    act(() => {
      useAppStore.getState().toggleGridSelection('item1', true);
    });
    expect(useAppStore.getState().gridSelection.has('item1')).toBe(false);
  });

  it('toggleGridSelection with multi=true preserves other items', () => {
    act(() => {
      useAppStore.getState().setGridSelection(new Set(['item1', 'item2']));
      useAppStore.getState().toggleGridSelection('item3', true);
    });
    expect(useAppStore.getState().gridSelection.has('item1')).toBe(true);
    expect(useAppStore.getState().gridSelection.has('item3')).toBe(true);
  });

  it('toggleCategoryCollapse toggles category state', () => {
    act(() => {
      useAppStore.getState().toggleCategoryCollapse('cat1');
    });
    expect(useAppStore.getState().collapsedCategories.has('cat1')).toBe(true);

    act(() => {
      useAppStore.getState().toggleCategoryCollapse('cat1');
    });
    expect(useAppStore.getState().collapsedCategories.has('cat1')).toBe(false);
  });

  it('setActiveGameId resets explorer and sidebar states and calls invoke', async () => {
    const invoke = (await import('@tauri-apps/api/core')).invoke;

    // Set some state to test resetting
    act(() => {
      useAppStore.setState({
        explorerSubPath: '/sub',
        currentPath: ['a', 'b'],
        explorerSearchQuery: 'search',
        selectedObject: 'obj',
        gridSelection: new Set(['1']),
        sidebarSearchQuery: 'side',
        selectedObjectType: 'type',
        collapsedCategories: new Set(['cat1']),
      });
    });

    await act(async () => {
      await useAppStore.getState().setActiveGameId('game1');
    });

    const state = useAppStore.getState();
    expect(state.activeGameId).toBe('game1');
    expect(state.explorerSubPath).toBeUndefined();
    expect(state.currentPath).toEqual([]);
    expect(state.explorerSearchQuery).toBe('');
    expect(state.selectedObject).toBeNull();
    expect(state.gridSelection.size).toBe(0);
    expect(state.sidebarSearchQuery).toBe('');
    expect(state.selectedObjectType).toBeNull();
    expect(state.collapsedCategories.size).toBe(0);

    expect(invoke).toHaveBeenCalledWith('set_active_game', { gameId: 'game1' });
  });

  it('setSafeMode calls invoke and updates state', async () => {
    const invoke = (await import('@tauri-apps/api/core')).invoke;

    await act(async () => {
      await useAppStore.getState().setSafeMode(false);
    });

    expect(useAppStore.getState().safeMode).toBe(false);
    expect(invoke).toHaveBeenCalledWith('set_safe_mode_enabled', { enabled: false });
  });

  it('setAutoCloseLauncher calls invoke and updates state', async () => {
    const invoke = (await import('@tauri-apps/api/core')).invoke;

    await act(async () => {
      await useAppStore.getState().setAutoCloseLauncher(true);
    });

    expect(useAppStore.getState().autoCloseLauncher).toBe(true);
    expect(invoke).toHaveBeenCalledWith('set_auto_close_launcher', { enabled: true });
  });

  it('initStore fetches settings from backend', async () => {
    const invoke = (await import('@tauri-apps/api/core')).invoke;
    vi.mocked(invoke).mockResolvedValueOnce({
      active_game_id: 'game-123',
      safe_mode: { enabled: true },
      auto_close_launcher: true,
    });

    await act(async () => {
      await useAppStore.getState().initStore();
    });

    const state = useAppStore.getState();
    expect(state.activeGameId).toBe('game-123');
    expect(state.safeMode).toBe(true);
    expect(state.autoCloseLauncher).toBe(true);
    expect(state.isStoreInitialized).toBe(true);
    expect(invoke).toHaveBeenCalledWith('get_settings');
  });

  it('openConflictDialog displays dialog with conflict data', () => {
    const mockConflict = {
      type: 'RenameConflict',
      attempted_target: 'a',
      existing_path: 'b',
      base_name: 'c',
    } as any;

    act(() => {
      useAppStore.getState().openConflictDialog(mockConflict);
    });

    expect(useAppStore.getState().conflictDialog.open).toBe(true);
    expect(useAppStore.getState().conflictDialog.conflict).toEqual(mockConflict);
  });
});

import { describe, it, expect, beforeEach } from 'vitest';
import { useScannerStore } from './useScannerStore';
import { act } from '@testing-library/react';

describe('useScannerStore', () => {
  beforeEach(() => {
    // Reset store before each test
    act(() => {
      useScannerStore.getState().resetScanner();
    });
  });

  it('initializes with default state', () => {
    const state = useScannerStore.getState();
    expect(state.isScanning).toBe(false);
    expect(state.progress.current).toBe(0);
    expect(state.progress.total).toBe(0);
    expect(state.scanResults).toEqual([]);
    expect(state.stats).toEqual({ matched: 0, unmatched: 0 });
  });

  it('setIsScanning updates scanning state', () => {
    act(() => {
      useScannerStore.getState().setIsScanning(true);
    });
    expect(useScannerStore.getState().isScanning).toBe(true);

    act(() => {
      useScannerStore.getState().setIsScanning(false);
    });
    expect(useScannerStore.getState().isScanning).toBe(false);
  });

  it('updateProgress updates progress state', () => {
    act(() => {
      useScannerStore.getState().updateProgress(5, 'test_folder', 5000);
    });

    const { progress } = useScannerStore.getState();
    expect(progress.current).toBe(5);
    expect(progress.folderName).toBe('test_folder');
    expect(progress.etaMs).toBe(5000);
    expect(progress.label).toContain('Scanning test_folder...');
    expect(progress.label).toContain('~5s remaining');
  });

  it('updateProgress formats minutes correctly', () => {
    act(() => {
      useScannerStore.getState().updateProgress(5, 'test_folder', 65000);
    });

    expect(useScannerStore.getState().progress.label).toContain('~2m remaining');
  });

  it('setTotalFolders updates total progress', () => {
    act(() => {
      useScannerStore.getState().setTotalFolders(100);
    });

    expect(useScannerStore.getState().progress.total).toBe(100);
    expect(useScannerStore.getState().progress.label).toBe('Found 100 folders to scan');
  });

  const mockResult = { folderPath: '/test1' } as any;
  act(() => {
    useScannerStore.getState().addScanResult(mockResult);
  });

  expect(useScannerStore.getState().scanResults).toEqual([{ folderPath: '/test1' }]);

  const mockResult2 = { folderPath: '/test2' } as any;
  act(() => {
    useScannerStore.getState().addScanResult(mockResult2);
  });

  expect(useScannerStore.getState().scanResults).toEqual([mockResult, mockResult2]);
});

it('setScanResults overwrites results array', () => {
  const results = [{ folderPath: '/test1' } as any, { folderPath: '/test2' } as any];

  act(() => {
    useScannerStore.getState().setScanResults(results);
  });

  expect(useScannerStore.getState().scanResults).toEqual(results);
});

it('setStats updates statistics', () => {
  act(() => {
    useScannerStore.getState().setStats(10, 5);
  });

  expect(useScannerStore.getState().stats).toEqual({ matched: 10, unmatched: 5 });
});
it('resetScanner restores default state', () => {
  act(() => {
    useScannerStore.getState().setIsScanning(true);
    useScannerStore.getState().updateProgress(50, 'folder', 100);
    useScannerStore.getState().setStats(10, 5);
    useScannerStore.getState().resetScanner();
  });

  const state = useScannerStore.getState();
  expect(state.isScanning).toBe(false);
  expect(state.progress.current).toBe(0);
  expect(state.progress.total).toBe(0);
  expect(state.stats).toEqual({ matched: 0, unmatched: 0 });
  expect(state.progress.label).toBe('Ready to scan');
});

import React, { ReactElement } from 'react';
import { render, RenderOptions } from '@testing-library/react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';

// Create a new QueryClient for each test to avoid contamination
const createTestQueryClient = () =>
  new QueryClient({
    defaultOptions: {
      queries: {
        retry: false, // Turn off retries for testing
      },
    },
  });

const AllTheProviders = ({ children }: { children: React.ReactNode }) => {
  const queryClient = createTestQueryClient();

  return <QueryClientProvider client={queryClient}>{children}</QueryClientProvider>;
};

const customRender = (ui: ReactElement, options?: Omit<RenderOptions, 'wrapper'>) =>
  render(ui, { wrapper: AllTheProviders, ...options });

// Re-export everything
export * from '@testing-library/react';

// Override render method
export { customRender as render };

// Export wrapper for hooks
export { AllTheProviders as createWrapper };

import { describe, it, expect, vi, beforeEach } from 'vitest';

describe('searchWorker', () => {
  let onmessageHandler: (e: any) => void;
  let postMessageSpy: ReturnType<typeof vi.fn>;

  beforeEach(async () => {
    postMessageSpy = vi.fn();

    Object.defineProperty(globalThis, 'self', {
      value: {
        postMessage: postMessageSpy,
        set onmessage(handler: any) {
          onmessageHandler = handler;
        },
      },
      writable: true,
      configurable: true,
    });

    vi.resetModules();
    // Dynamic import to execute the script in the mocked self environment
    await import('./searchWorker');
  });

  it('should return null ids if query is empty', () => {
    onmessageHandler({
      data: {
        objects: [{ id: '1', name: 'Alhaitham' }],
        query: '   ',
      },
    });

    expect(postMessageSpy).toHaveBeenCalledWith({ ids: null });
  });

  it('should return matched ids for single token', () => {
    const objects = [
      { id: '1', name: 'Alhaitham' },
      { id: '2', name: 'Amber' },
      { id: '3', name: 'Albedo' },
    ];

    onmessageHandler({
      data: {
        objects,
        query: 'alb',
      },
    });

    expect(postMessageSpy).toHaveBeenCalledWith({ ids: ['3'] });
  });

  it('should handle multi-token substring matching case-insensitively', () => {
    const objects = [
      { id: '1', name: 'Genshin Impact Alhaitham Mod' },
      { id: '2', name: 'Alhaitham Default Outfit' },
      { id: '3', name: 'Amber Outfit' },
    ];

    onmessageHandler({
      data: {
        objects,
        query: 'Alhaitham mod',
      },
    });

    expect(postMessageSpy).toHaveBeenCalledWith({ ids: ['1'] });
  });

  it('should return empty array if no match', () => {
    const objects = [
      { id: '1', name: 'Alhaitham' },
      { id: '2', name: 'Amber' },
    ];

    onmessageHandler({
      data: {
        objects,
        query: 'Zhongli',
      },
    });

    expect(postMessageSpy).toHaveBeenCalledWith({ ids: [] });
  });
});
